import React, { useState } from "react";

const Payment = () => {
  const [paymentUrl, setPaymentUrl] = useState("");

  const handleConfirmPayment = () => {
    fetch("/api/payment/create_payment")
      .then((response) => {
        if (!response.ok) {
          throw new Error("Network response was not ok");
        }
        return response.json();
      })
      .then((data) => {
        setPaymentUrl(data.URL);
      })
      .catch((error) => {
        console.error("Error:", error);
        // Handle error, show error message to the user, etc.
      });
  };

  return (
    <section className="section-content padding-y">
      <div className="container" style={{ maxWidth: "720p" }}>
        <div className="card mb-4">
          <div className="card-body">
            <h4 className="card-title mb-3">Delivery info</h4>
            <div className="form-row">
              <div className="col form-group">
                <label>Full name</label>
                <input
                  type="text"
                  className="form-control"
                  name="fullName"
                  // value={formData.fullName}
                  // onChange={handleInputChange}
                />
              </div>
            </div>
            <div className="form-row">
              <div className="col form-group">
                <label>Email</label>
                <input
                  type="email"
                  className="form-control"
                  name="email"
                  // value={formData.email}
                  // onChange={handleInputChange}
                />
              </div>
              <div className="col form-group">
                <label>Phone</label>
                <input
                  type="text"
                  className="form-control"
                  name="phone"
                  value={formData.phone}
                  onChange={handleInputChange}
                />
              </div>
            </div>
            <div className="form-row">
              <div className="col form-group">
                <label>Address</label>
                <textarea
                  className="form-control"
                  rows="2"
                  name="address"
                  value={formData.address}
                  onChange={handleInputChange}
                ></textarea>
              </div>
            </div>
          </div>
        </div>
        <div className="card mb-4">
          <div className="card-body">
            <h4 className="card-title mb-4">Payment</h4>
            <form role="form" style={{ maxWidth: "380px" }}>
              <div className="form-group">
                <label htmlFor="cardNumber">Card number</label>
                <input
                  type="text"
                  className="form-control"
                  name="cardNumber"
                  value={formData.cardNumber}
                  onChange={handleInputChange}
                />
              </div>
              {/* Remaining fields for card expiration and CVV */}
              <button
                className="btn btn-primary btn-block"
                type="button"
                onClick={handleConfirmPayment}
              >
                Confirm
              </button>
              {paymentUrl && (
                <div className="container">
                  <p>Redirecting to payment page...</p>
                  {/* You can use paymentUrl here for redirection or display */}
                </div>
              )}
            </form>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Payment;
