import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

const DeliveryInfor = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    deliveryMethod: "standard",
    firstName: "",
    lastName: "",
    email: "",
    phone: "",
    country: "United States",
    city: "",
    address: "",
    cardName: "",
    cardNumber: "",
    cardExpiryMonth: "",
    cardExpiryYear: "",
    cardCVV: "",
  });

  const [paymentMethod, setPaymentMethod] = useState("cod"); // Default to COD

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (paymentMethod === "momo") {
      try {
        const momoResponse = await initiateMoMoPayment();
        if (momoResponse && momoResponse.payUrl) {
          window.location.href = momoResponse.payUrl;
        } else {
          console.error("Error initiating MoMo payment:", momoResponse);
        }
      } catch (error) {
        console.error("Error:", error.message);
      }
    } else {
      // Handle COD payment here
      alert("COD payment method selected");
    }
  };

  const initiateMoMoPayment = async () => {
    const paymentData = {
      orderInfo: "Thanh toán đơn hàng",
      amount: 1000, // Replace with actual amount
      orderId: new Date().getTime(),
      requestId: new Date().getTime(),
      returnUrl: "http://localhost:3000/payment-success",
      notifyUrl: "http://localhost:8080/api/momo/notify",
      extraData: "",
    };

    try {
      const response = await fetch("http://localhost:8080/api/momo/payment", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(paymentData),
      });

      if (response.ok) {
        const momoResponse = await response.json();
        return momoResponse;
      } else {
        console.error("Error initiating MoMo payment:", response.status, response.statusText);
      }
    } catch (error) {
      console.error("Error:", error.message);
    }
  };

  return (
    <section className="section-content padding-y">
      <div className="container" style={{ maxWidth: "720px" }}>
        <div className="card mb-4">
          <div className="card-body">
            <h4 className="card-title mb-3">Delivery info</h4>
            <div className="form-row">
              <div className="form-group col-sm-6">
                <label className="js-check box active">
                  <input type="radio" name="deliveryMethod" value="standard" checked={formData.deliveryMethod === "standard"} onChange={handleChange} />
                  <h6 className="title">Standard delivery</h6>
                  <p className="text-muted">Free by airline within 20 days</p>
                </label>
              </div>
              <div className="form-group col-sm-6">
                <label className="js-check box">
                  <input type="radio" name="deliveryMethod" value="fast" checked={formData.deliveryMethod === "fast"} onChange={handleChange} />
                  <h6 className="title">Fast delivery</h6>
                  <p className="text-muted">Extra 20$ will be charged</p>
                </label>
              </div>
            </div>

            <div className="form-row">
              <div className="col form-group">
                <label>First name</label>
                <input type="text" className="form-control" name="firstName" value={formData.firstName} onChange={handleChange} />
              </div>
              <div className="col form-group">
                <label>Last name</label>
                <input type="text" className="form-control" name="lastName" value={formData.lastName} onChange={handleChange} />
              </div>
            </div>

            <div className="form-row">
              <div className="col form-group">
                <label>Email</label>
                <input type="email" className="form-control" name="email" value={formData.email} onChange={handleChange} />
              </div>
              <div className="col form-group">
                <label>Phone</label>
                <input type="text" className="form-control" name="phone" value={formData.phone} onChange={handleChange} />
              </div>
            </div>

            <div className="form-row">
              <div className="form-group col-md-6">
                <label>Country</label>
                <select id="inputState" className="form-control" name="country" value={formData.country} onChange={handleChange}>
                  <option> Choose...</option>
                  <option>Uzbekistan</option>
                  <option>Russia</option>
                  <option>United States</option>
                  <option>India</option>
                  <option>Afganistan</option>
                </select>
              </div>
              <div className="form-group col-md-6">
                <label>City</label>
                <input type="text" className="form-control" name="city" value={formData.city} onChange={handleChange} />
              </div>
            </div>
            <div className="form-group">
              <label>Address</label>
              <textarea className="form-control" rows="2" name="address" value={formData.address} onChange={handleChange}></textarea>
            </div>
          </div>
        </div>

        <div className="card mb-4">
          <div className="card-body">
            <h4 className="card-title mb-4">Payment</h4>
            <form role="form" style={{ maxWidth: "380px" }} onSubmit={handleSubmit}>
              <div className="form-group">
                <label htmlFor="username">Name on card</label>
                <input type="text" className="form-control" name="cardName" placeholder="Ex. John Smith" required value={formData.cardName} onChange={handleChange} />
              </div>

              <div className="form-group">
                <label htmlFor="cardNumber">Card number</label>
                <div className="input-group">
                  <input type="text" className="form-control" name="cardNumber" placeholder="" value={formData.cardNumber} onChange={handleChange} />
                  <div className="input-group-append">
                    <span className="input-group-text">
                      <i className="fab fa-cc-visa"></i> &nbsp; <i className="fab fa-cc-amex"></i> &nbsp; 
                      <i className="fab fa-cc-mastercard"></i> 
                    </span>
                  </div>
                </div>
              </div>

              <div className="row">
                <div className="col-md flex-grow-0">
                  <div className="form-group">
                    <label className="hidden-xs">Expiration</label>
                    <div className="form-inline" style={{ maxWidth: "200px" }}>
                      <select className="form-control" style={{ width: "100px" }} name="cardExpiryMonth" value={formData.cardExpiryMonth} onChange={handleChange}>
                        <option>MM</option>
                        <option>01 - January</option>
                        <option>02 - February</option>
                        <option>03 - March</option>
                      </select>
                      <span style={{ width: "20px" }}> / </span>
                      <select className="form-control" style={{ maxWidth: "100px" }} name="cardExpiryYear" value={formData.cardExpiryYear} onChange={handleChange}>
                        <option>YY</option>
                        <option>2023</option>
                        <option>2024</option>
                        <option>2025</option>
                      </select>
                    </div>
                  </div>
                </div>
                <div className="col-md-3">
                  <div className="form-group">
                    <label data-toggle="tooltip" title="3 digits code on back side of the card">CVV <i className="fa fa-question-circle"></i></label>
                    <input className="form-control" required type="text" name="cardCVV" value={formData.cardCVV} onChange={handleChange} />
                  </div>
                </div>
              </div>
              <div className="form-group">
                <label>Payment Method</label>
                <select className="form-control" name="paymentMethod" value={paymentMethod} onChange={(e) => setPaymentMethod(e.target.value)}>
                  <option value="cod">COD</option>
                  <option value="momo">MoMo</option>
                </select>
              </div>
              <button className="btn btn-primary btn-block" type="submit">Confirm</button>
            </form>
          </div>
        </div>
      </div>
    </section>
  );
};

export default DeliveryInfor;
