import React, { useState } from "react";

const Payment = () => {
  const [paymentUrl, setPaymentUrl] = useState("");

  const handleConfirmPayment = () => {
    fetch("/api/payment/create_payment")
      .then((response) => {
        if (!response.ok) {
          throw new Error("Network response was not ok");
        }
        return response.json();
      })
      .then((data) => {
        setPaymentUrl(data.URL);
        // Redirect to paymentUrl
        window.location.href = data.URL;
      })
      .catch((error) => {
        console.error("Error:", error);
        // Handle error, show error message to the user, etc.
      });
  };

  return (
    <section class="section-content padding-y">
    <div class="container" style={{ maxWidth: "720p" }}>
      <div class="card mb-4">
        <div class="card-body">
          <h4 class="card-title mb-3">Delivery info</h4>
          {/* Các trường thông tin giao hàng */}
        </div>
      </div>

      <div class="card mb-4">
        <div class="card-body">
          <h4 class="card-title mb-4">Payment</h4>
          <form role="form" style={{ maxWidth: "380px" }}>
            {/* Form thanh toán */}
            <button
              className="btn btn-primary btn-block"
              type="button"
              onClick={handleConfirmPayment}
            >
              Confirm
            </button>
          </form>
        </div>
      </div>
    </div>
    </section>
  );
};

export default Payment;
