import React, { useState, useEffect } from "react";
import { useNavigate, useLocation } from "react-router-dom";

const FormCart = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    fullname: "",
    email: "",
    phone_number: "",
    address: "",
    note: "",
    order_date: "",
    thumbnail: "",
    total_money: "",
    shipping_method: "cod",
  });

  useEffect(() => {
    const email = localStorage.getItem("email") || "";
    const fullname = localStorage.getItem("fullname") || "";
    const address = localStorage.getItem("address") || "";
    const phone_number = localStorage.getItem("phone_number") || "";

    setFormData((prevData) => ({
      ...prevData,
      email: email,
      fullname: fullname,
      address: address,
      phone_number: phone_number,
    }));
  }, []);

  useEffect(() => {
    const cartItems = JSON.parse(localStorage.getItem("cartItems")) || [];
    const today = new Date();
    const formattedDate = today.toISOString().split('T')[0];

    const totalMoney = cartItems.reduce(
      (accumulator, currentItem) => accumulator + currentItem.price * currentItem.quantity,
      0
    );

    const thumbnails = cartItems.map(item => item.thumbnail);

    setFormData((prevData) => ({
      ...prevData,
      total_money: totalMoney,
      order_date: formattedDate,
      thumbnail: thumbnails.join(", "),
    }));
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!validateForm()) {
      alert("Please fill out all required fields correctly.");
      return;
    }

    if (formData.shipping_method === "payment") {
      // Navigate to the payment page
      navigate("/payment");
    } else {
      try {
        const response = await fetch("http://localhost:8080/api/orders", {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
            "Authorization": `Bearer ${localStorage.getItem("token")}`,
          },
          body: JSON.stringify(formData),
        });

        if (response.ok) {
          console.log("Order placed successfully!");
          setFormData({
            fullname: "",
            email: "",
            phone_number: "",
            address: "",
            note: "",
            order_date: "",
            thumbnail: "",
            total_money: "",
            shipping_method: "cod",
          });
          localStorage.removeItem("cartItems");
          alert("Order placed successfully!");
          navigate("/");
          window.location.reload();
        } else {
          alert("Error placing order. Please try again.");
        }
      } catch (error) {
        console.error("Error:", error.message);
        alert("An error occurred. Please try again.");
      }
    }
  };

  const validateForm = () => {
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    const phoneRegex = /^[0-9]{10,11}$/;

    return (
      formData.fullname.trim() !== "" &&
      emailRegex.test(formData.email.trim()) &&
      phoneRegex.test(formData.phone_number.trim()) &&
      formData.address.trim() !== ""
    );
  };

  return (
    <div className="container">
      <h2>Order Information</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="fullname">Full Name:</label>
          <input
            type="text"
            id="fullname"
            name="fullname"
            className="form-control"
            value={formData.fullname}
            onChange={handleChange}
          />
        </div>
        <div className="form-group">
          <label htmlFor="email">Email:</label>
          <input
            type="email"
            id="email"
            name="email"
            className="form-control"
            value={formData.email}
            onChange={handleChange}
          />
        </div>
        <div className="form-group">
          <label htmlFor="phone_number">Phone Number:</label>
          <input
            type="text"
            id="phone_number"
            name="phone_number"
            className="form-control"
            value={formData.phone_number}
            onChange={handleChange}
          />
        </div>
        <div className="form-group">
          <label htmlFor="address">Shipping Address:</label>
          <textarea
            id="address"
            name="address"
            className="form-control"
            value={formData.address}
            onChange={handleChange}
          />
        </div>
        <div className="form-group">
          <label htmlFor="note">Notes:</label>
          <textarea
            id="note"
            name="note"
            className="form-control"
            value={formData.note}
            onChange={handleChange}
          />
        </div>
        <div className="form-group">
          <label htmlFor="order_date">Order Date:</label>
          <input
            type="date"
            id="order_date"
            name="order_date"
            className="form-control"
            value={formData.order_date}
            onChange={handleChange}
          />
        </div>
        <div className="form-group">
          <label htmlFor="thumbnail">Thumbnails:</label>
          <input
            type="text"
            id="thumbnail"
            name="thumbnail"
            className="form-control"
            value={formData.thumbnail}
            onChange={handleChange}
          />
        </div>
        <div className="form-group">
          <label htmlFor="total_money">Total Amount:</label>
          <input
            type="text"
            id="total_money"
            name="total_money"
            className="form-control"
            value={formData.total_money}
            onChange={handleChange}
            readOnly
          />
        </div>
        <div className="form-group">
          <label htmlFor="shipping_method">Payment Method:</label>
          <select
            id="shipping_method"
            name="shipping_method"
            className="form-control"
            value={formData.shipping_method}
            onChange={handleChange}
          >
            <option value="cod">COD</option>
            <option value="payment">Payment</option>
          </select>
        </div>
        <button type="submit" className="btn btn-primary">
          Place Order
        </button>
      </form>
    </div>
  );
};

export default FormCart;
