import React, { useState } from "react";
import axios from "axios";

const Payment = () => {
  const [paymentURL, setPaymentURL] = useState(""); // State to store payment URL

  // Function to handle payment confirmation
  const handlePaymentConfirmation = () => {
    axios.get("/api/payment/create_payment")
      .then(response => {
        const { data } = response; // Extract data from response
        const { URL } = data; // Extract URL from response data
        setPaymentURL(URL); // Update state with payment URL
      })
      .catch(error => {
        console.error("Error creating payment:", error); // Log any errors
        // Handle errors or display error message to user
      });
  };

  return (
    <section className="section-content padding-y">
      {/* Your existing JSX code */}
      <button className="btn btn-primary btn-block" type="button" onClick={handlePaymentConfirmation}>
        Confirm
      </button>
      {/* Conditionally render payment URL if available */}
      {paymentURL && (
        <div>
          <h4>Payment URL:</h4>
          <a href={paymentURL} target="_blank" rel="noopener noreferrer">{paymentURL}</a>
        </div>
      )}
    </section>
  );
};

export default Payment;
