import React, { useEffect, useState } from "react";
import { GET_ALL } from "../api/apiService";
import startsActive from "../../assets/images/icons/pay-american-ex.png";
import startsDisable from "../../assets/images/icons/pay-bank.png";
import { Link } from "react-router-dom";
const cardTextStyle = {
  maxWidth: "80%",
};
const LoadProduct = (category) => {
  const { categoryName, categoryId } = category;
  const [products, setProducts] = useState([]);
  useEffect(() => {
    GET_ALL(`products/getlatest?categoryid=${categoryId}`).then((item) =>
      setProducts(item.data)
    );
  }, [categoryId]);
  return (
    <section class="padding-bottom">
      <header class="section-heading mb-4">
        <h3 class="title-section">{categoryName}</h3>
      </header>
      <div class="row large-columns-4 medium-columns-2 small-columns-2 row-small">
        {products.length > 0 &&
          products.map((row) => (
            <div
              class="product-small col has-hover out-of-stock product type-product post-61896 status-publish first outofstock product_cat-do-luu-niem-mixi has-post-thumbnail sold-individually shipping-taxable purchasable product-type-variable"
              key={row.id}
            >
               <div class="badge-container absolute left top z-1">
                  <div class="callout badge badge-circle">
                    <div class="badge-inner secondary on-sale">
                      <span class="onsale">Giảm giá!</span>
                    </div>
                  </div>
                </div>
              <div class="card card-product-grid">

                <Link
                  to={`/detailProduct?productId=${row.id}`}
                  class="img-wrap"
                >
                  <img src={`./images/items/${row.thumbnail}`} />
                </Link>
                <figcaption class="info-wrap">
                  <ul class="rating-stars mb-1">
                    <li style={{ cardTextStyle }} class="stars-active">
                      <img src={startsActive} alt="" />
                    </li>
                    <li>
                      <img src={startsDisable} alt="" />
                    </li>
                  </ul>
                  <div>
                    <Link
                      to={`/detailProduct?productId=${row.id}`}
                      class="title"
                    >
                      {row.title}
                    </Link>
                  </div>
                  <div class="price h5 mt-2">${row.price}</div>
                </figcaption>
              </div>
            </div>
          ))}
      </div>
    </section>
  );
};
export default LoadProduct;
