import React, { useState } from 'react';
import { Navigate } from 'react-router-dom';

function PaymentComponent() {
    const [paymentResult, setPaymentResult] = useState(null);

    const createPayment = async () => {
        try {
            const response = await fetch('http://localhost:8080/api/payment/create_payment');
            if (response.ok) {
                throw new Error('Failed to fetch payment URL');
            }
            const data = await response.json();
            setPaymentResult(data);
            Navigate
        } catch (error) {
            console.error('Error creating payment:', error);
        }
    };

    return (
        <div>
            <button onClick={createPayment}>Create Payment</button>
            {paymentResult && (
                <div>
                    <p>Status: {paymentResult.status}</p>
                    <p>Message: {paymentResult.message}</p>
                    <p>URL: <a href={paymentResult.URL}>{paymentResult.URL}</a></p>
                </div>
            )}
        </div>
    );
}

export default PaymentComponent;
