import React, { useEffect, useState } from "react";
import { GET_ALL } from "../api/apiService";

import { Link } from "react-router-dom";

const cardTextStyle = {
  maxWidth: "80%",
};

const LoadProduct = (category) => {
  const { categoryName, categoryId } = category;
  const [products, setProducts] = useState([]);

  useEffect(() => {
    GET_ALL(`products/getlatest?categoryid=${categoryId}`).then((item) =>
      setProducts(item.data)
    );
  }, [categoryId]);

  // State để lưu trữ số sao đánh giá của mỗi sản phẩm
  const [ratings, setRatings] = useState({});

  // Hàm cập nhật số sao đánh giá cho mỗi sản phẩm
  const handleRatingChange = (productId, rating) => {
    setRatings((prevRatings) => ({
      ...prevRatings,
      [productId]: rating,
    }));
  };

  return (
    <section className="padding-bottom">
      <header className="section-heading mb-4">
        <h3 className="title-section">{categoryName}</h3>
      </header>
      <div className="row">
        {products.length > 0 &&
          products.map((row) => (
            <div className="col-xl-3 col-lg-3 col-md-4 col-6" key={row.id}>
              <div className="card card-product-grid">
                <Link to={`/detailProduct?productId=${row.id}`} className="img-wrap">
                  <img src={`./images/items/${row.thumbnail}`} alt={row.title} className="img-fluid" />
                </Link>
                <figcaption className="info-wrap">
                  <div className="rating-stars mb-1">
                    {/* Hiển thị số sao đánh giá */}
                    <div className="horizontal-ratings">
                      {[...Array(5)].map((star, index) => {
                        const ratingValue = index + 1;
                        return (
                          <span
                            key={index}
                            className={ratingValue <= (ratings[row.id] || 0) ? 'star-filled' : 'star-empty'}
                            onClick={() => handleRatingChange(row.id, ratingValue)}
                          >
                            &#9733;
                          </span>
                        );
                      })}
                    </div>
                  </div>
                  <div>
                    <Link to={`/detailProduct?productId=${row.id}`} className="title">{row.title}</Link>
                  </div>
                  <div className="price h5 mt-2">${row.price}</div>
                </figcaption>
              </div>
            </div>
          ))}
      </div>
    </section>
  );
};

export default LoadProduct;
