import React, { useState } from 'react';
import axios from 'axios';

function PaymentComponent() {
  const [paymentUrl, setPaymentUrl] = useState('');
  const [errorMessage, setErrorMessage] = useState('');

  const handleCreatePayment = async () => {
    try {
      const response = await axios.get('http://localhost:8080/api/payment/create_payment');
      const { data } = response;
      if (data && data.URL) {
        setPaymentUrl(data.URL);
        setErrorMessage('');
      } else {
        setErrorMessage('Failed to retrieve payment URL');
      }
    } catch (error) {
      setErrorMessage('Error occurred while fetching payment URL');
    }
  };

  return (
    <div>
      <button onClick={handleCreatePayment}>Create Payment</button>
      {errorMessage && <div>{errorMessage}</div>}
      {paymentUrl && (
        <div>
          <p>Payment URL:</p>
          <a href={paymentUrl} target="_blank" rel="noopener noreferrer">{paymentUrl}</a>
        </div>
      )}
    </div>
  );
}

export default PaymentComponent;
