import React, { useState } from "react";

const Payment = () => {
  const [paymentUrl, setPaymentUrl] = useState("");

  const handleConfirmPayment = () => {
    fetch("/api/payment/create_payment")
      .then((response) => {
        if (!response.ok) {
          throw new Error("Network response was not ok");
        }
        return response.json();
      })
      .then((data) => {
        setPaymentUrl(data.URL);
      })
      .catch((error) => {
        console.error("Error:", error);
        // Handle error, show error message to the user, etc.
      });
  };

  return (
    <section className="section-content padding-y">
      {/* Your existing HTML for the Payment form */}
      <div className="container" style={{ maxWidth: "720p" }}>
        {/* Your existing HTML content for the Payment form */}
        <button
          className="btn btn-primary btn-block"
          type="button"
          onClick={handleConfirmPayment}
        >
          Confirm
        </button>
      </div>
      {paymentUrl && (
        <div className="container">
          <p>Redirecting to payment page...</p>
          {/* You can use paymentUrl here for redirection or display */}
        </div>
      )}
    </section>
  );
};

export default Payment;
