import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from 'react-router-dom';

const Register = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [fullname, setFullname] = useState("");
  const [address, setAddress] = useState("");
  const [phone_number, setPhoneNumber] = useState("");
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleRegister = () => {
    if (email !== "" && password !== "" && fullname !== "" && address !== "" && phone_number !== "") {
      axios
        .post(`http://localhost:8080/api/users`, {
          email,
          password,
          fullname,
          address,
          phone_number,
        })
        .then((response) => {
          if (response.status === 200) {
            alert("Đăng ký thành công");

            // Lưu thông tin người dùng vào localStorage
            localStorage.setItem("email", email);
            localStorage.setItem("fullname", fullname);
            localStorage.setItem("address", address);
            localStorage.setItem("phone_number", phone_number);

            navigate('/login');
          } else {
            setError("Có lỗi xảy ra. Vui lòng thử lại sau.");
          }
        })
        .catch((error) => {
          console.error(error);
          setError("Có lỗi xảy ra. Vui lòng thử lại sau.");
        });
    } else {
      setError("Vui lòng điền đầy đủ thông tin.");
    }
  };

  return (
    <section className="section-content padding-y">
      <div className="card mx-auto" style={{ width: '520px' }}>
        <article className="card-body">
          <header className="mb-4"><h4 className="card-title">Đăng ký</h4></header>
          <form className="mx-auto">
            <label>Họ và tên</label>
            <input
              required
              className="form-control"
              type="fullname"
              placeholder="Họ và tên"
              value={fullname}
              onChange={(e) => setFullname(e.target.value)}
            />

            <label>Email</label>
            <input
              required
              className="form-control"
              type="email"
              placeholder="Email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />

            <label>Mật khẩu</label>
            <input
              required
              className="form-control"
              type="password"
              placeholder="Mật khẩu"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />

            <label>Địa chỉ</label>
            <input
              required
              className="form-control"
              type="address"
              placeholder="Địa chỉ"
              value={address}
              onChange={(e) => setAddress(e.target.value)}
            />

            <label>Số điện thoại</label>
            <input
              required
              className="form-control"
              type="phone_number"
              placeholder="Số điện thoại"
              value={phone_number}
              onChange={(e) => setPhoneNumber(e.target.value)}
            />

            <div className="mt-3">
              {error && <p className="text-danger">{error}</p>}
              <button onClick={handleRegister} type="button" className="btn btn-primary btn-block"> Đăng ký </button>
            </div>
          </form>
        </article>
      </div>
    </section>
  );
};

export default Register;
