import React, { useState } from 'react';
import axios from 'axios';

function PaymentComponent() {
  const [paymentInfo, setPaymentInfo] = useState(null);
  const [errorMessage, setErrorMessage] = useState('');

  const handleCreatePayment = async () => {
    try {
      const response = await axios.get('http://localhost:8080/api/payment/create_payment');
      const { data } = response;
      if (data && data.status === 'OK') {
        setPaymentInfo(data);
        setErrorMessage('');
      } else {
        setErrorMessage(data.message || 'Failed to retrieve payment info');
      }
    } catch (error) {
      setErrorMessage('Error occurred while fetching payment info');
    }
  };

  return (
    <div>
      <button onClick={handleCreatePayment}>Create Payment</button>
      {errorMessage && <div>{errorMessage}</div>}
      {paymentInfo && (
        <div>
          <p>Status: {paymentInfo.status}</p>
          <p>Message: {paymentInfo.message}</p>
          <p>URL: <a href={paymentInfo.URL} target="_blank" rel="noopener noreferrer">{paymentInfo.URL}</a></p>
          {/* Add more fields as needed */}
        </div>
      )}
    </div>
  );
}

export default PaymentComponent;
