import React, { useState, useEffect } from 'react';
import axios from 'axios';

function PaymentComponent() {
  const [paymentResult, setPaymentResult] = useState(null);

  useEffect(() => {
    async function fetchPaymentResult() {
      try {
        const response = await axios.get('/api/payment/create_payment');
        setPaymentResult(response.data);
      } catch (error) {
        console.error('Error fetching payment result:', error);
        // Xử lý lỗi ở đây
      }
    }

    fetchPaymentResult();
  }, []);

  return (
    <div>
      {paymentResult ? (
        <div>
          <p>Trạng thái: {paymentResult.status}</p>
          <p>Thông điệp: {paymentResult.message}</p>
          <p>URL thanh toán: <a href={paymentResult.URL}>{paymentResult.URL}</a></p>
        </div>
      ) : (
        <p>Đang tải...</p>
      )}
    </div>
  );
}

export default PaymentComponent;
