import React, { useState } from 'react';
import { Config } from './Config'; // Import Config từ module của bạn

function PaymentComponent() {
    const [paymentUrl, setPaymentUrl] = useState(null);

    const handleCreatePayment = async () => {
        try {
            const response = await fetch('/api/payment/create_payment');
            if (!response.ok) {
                throw new Error('Failed to create payment');
            }
            const data = await response.json();
            const secureHash = Config.hashAllFields(data); // Tính toán secure hash từ dữ liệu phản hồi
            const paymentUrlWithHash = `${data.URL}&vnp_SecureHash=${secureHash}`; // Thêm secure hash vào URL thanh toán
            setPaymentUrl(paymentUrlWithHash);
        } catch (error) {
            console.error('Error:', error);
            // Xử lý lỗi ở đây
        }
    };

    return (
        <div>
            <button onClick={handleCreatePayment}>Create Payment</button>
            {paymentUrl && (
                <div>
                    <p>Payment URL:</p>
                    <a href={paymentUrl} target="_blank" rel="noopener noreferrer">{paymentUrl}</a>
                </div>
            )}
        </div>
    );
}

export default PaymentComponent;
