import axios from "axios";
import React, { useEffect, useState } from "react";
import { unstable_HistoryRouter, useLocation, useNavigate } from "react-router-dom";
const FormCart = () => {
  const history = unstable_HistoryRouter();
  const [formData, setFormData] = useState({
    fullname: "",
    email: "",
    phone_number: "",
    address: "",
    note: "",
    order_date: "",
    thumbnail: "",
    total_money: "",
    shipping_method: "cod",
  });

  useEffect(() => {
    // Lấy thông tin người dùng từ localStorage trong trang form
    const email = localStorage.getItem("email") || "";
    const fullname = localStorage.getItem("fullname") || "";
    const address = localStorage.getItem("address") || "";
    const phone_number = localStorage.getItem("phone_number") || "";

    setFormData((prevData) => ({
      ...prevData,
      email: email,
      fullname: fullname,
      address: address,
      phone_number: phone_number,
    }));
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Kiểm tra nếu các trường bắt buộc đã được điền đầy đủ
    if (
      formData.fullname.trim() === "" ||
      formData.email.trim() === "" ||
      formData.phone_number.trim() === "" ||
      formData.address.trim() === ""
    ) {
      alert("Vui lòng điền đầy đủ thông tin trước khi đặt hàng.");
      return;
    }

    if (formData.shipping_method === "payment") {
      try {
        // Tạo yêu cầu thanh toán đến backend
        const response = await axios.get("http://localhost:8080/api/payment/create_payment");
        const { status, messenge, url } = response.data;
        if (status === "OK" && url) {
          // Chuyển hướng người dùng đến trang thanh toán
          window.location.href = url;
        } else {
          alert("Không thể lấy được URL thanh toán: " + messenge);
        }
      } catch (error) {
        console.error("Lỗi khi tạo thanh toán:", error);
        alert("Đã xảy ra lỗi khi tạo thanh toán");
      }
      // Sau khi chuyển hướng, không cần tiếp tục xử lý nữa
      return;
    }

    // Xử lý thanh toán COD
    try {
      // Gửi yêu cầu đặt hàng đến backend
      const response = await fetch("http://localhost:8080/api/orders", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          "Authorization": `Bearer ${localStorage.getItem("token")}`,
        },
        body: JSON.stringify(formData),
      });

      if (response.ok) {
        // Xử lý khi đặt hàng thành công
        console.log("Đặt hàng thành công!");
        // Xóa form sau khi đặt hàng thành công
        setFormData({
          fullname: "",
          email: "",
          phone_number: "",
          address: "",
          note: "",
          order_date: "",
          thumbnail: "",
          total_money: "",
          shipping_method: "cod",
        });

        // Xóa các sản phẩm trong giỏ hàng từ localStorage
        localStorage.removeItem("cartItems");

        // Hiển thị thông báo thành công cho người dùng
        alert("Đặt hàng thành công!");

        // Chuyển hướng sau khi đặt hàng thành công
        history.push("/profile/order");
        window.location.reload();
      } else {
        // Xử lý lỗi từ server
        console.error("Lỗi khi đặt hàng:", response.status, response.statusText);
      }
    } catch (error) {
      // Xử lý lỗi mạng hoặc lỗi khác
      console.error("Lỗi:", error.message);
    }
  };

  return (
    <div className="container">
      <h2>Thông tin đặt hàng</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="fullname">Họ và tên:</label>
          <input
            type="text"
            id="fullname"
            name="fullname"
            className="form-control"
            value={formData.fullname}
            onChange={handleChange}
          />
        </div>
        <div className="form-group">
          <label htmlFor="email">Email:</label>
          <input
            type="email"
            id="email"
            name="email"
            className="form-control"
            value={formData.email}
            onChange={handleChange}
          />
        </div>
        <div className="form-group">
          <label htmlFor="phone_number">Số điện thoại:</label>
          <input
            type="text"
            id="phone_number"
            name="phone_number"
            className="form-control"
            value={formData.phone_number}
            onChange={handleChange}
          />
        </div>
        <div className="form-group">
          <label htmlFor="address">Địa chỉ giao hàng:</label>
          <textarea
            id="address"
            name="address"
            className="form-control"
            value={formData.address}
            onChange={handleChange}
          />
        </div>
        <div className="form-group">
          <label htmlFor="note">Ghi chú:</label>
          <textarea
            id="note"
            name="note"
            className="form-control"
            value={formData.note}
            onChange={handleChange}
          />
        </div>
        <div className="form-group">
          <label htmlFor="order_date">Ngày đặt hàng:</label>
          <input
            type="date"
            id="order_date"
            name="order_date"
            className="form-control"
            value={formData.order_date}
            onChange={handleChange}
          />
        </div>
        <div className="form-group">
          <label htmlFor="thumbnail">Hình ảnh:</label>
          <input
            type="text"
            id="thumbnail"
            name="thumbnail"
            className="form-control"
            value={formData.thumbnail}
            onChange={handleChange}
          />
        </div>
        <div className="form-group">
          <label htmlFor="total_money">Tổng số tiền:</label>
          <input
            type="text"
            id="total_money"
            name="total_money"
            className="form-control"
            value={formData.total_money}
            onChange={handleChange}
            readOnly // Make the input read-only
          />
        </div>
        <div className="form-group">
          <label htmlFor="shipping_method">Phương thức thanh toán:</label>
          <select
            id="shipping_method"
            name="shipping_method"
            className="form-control"
            value={formData.shipping_method}
            onChange={handleChange}
          >
            <option value="cod">COD</option>
            <option value="payment">Payment</option>
          </select>
        </div>
        <button type="submit" className="btn btn-primary">
          Đặt hàng
        </button>
      </form>
    </div>
  );
};

export default FormCart;
