import axios from "axios";
import React, { useEffect, useState } from "react";

const Order = () => {

  const [orders, setOrders] = useState([]);



  useEffect(() => {
    axios
      .get("http://localhost:8080/api/orders")
      .then((response) => {
        setOrders(response.data);
      })
      .catch((error) => {
        console.error("Lấy danh sách đơn hàng thất bại:", error);
      });
  }, []);

  return (
    <section className="section-content padding-y">
      <div className="container">
        <div className="row">
          <aside className="col-md-3">
            <nav className="list-group">
              <a className="list-group-item" href="/profile/main">
                {" "}
                Account overview{" "}
              </a>
              <a className="list-group-item" href="/profile/address">
                {" "}
                My Address{" "}
              </a>
              <a className="list-group-item active" href="/profile/order">
                {" "}
                My Orders{" "}
              </a>
              <a className="list-group-item" href="/profile/wishlist">
                {" "}
                My wishlist{" "}
              </a>
              <a className="list-group-item" href="/profile/seller">
                {" "}
                My Selling Items{" "}
              </a>
              <a className="list-group-item" href="/profile/setting">
                {" "}
                Settings{" "}
              </a>

            </nav>
          </aside>
          <main className="col-md-9">
           {orders.map((order) => (
  <article key={order.id} className="card mb-4">
    <div className="card-body">
      <div>
        <div className="row">
          {order.products.map((product) => (
            <div key={product.id} className="col-md-3">
              <img
                src={product.image} // Thay đổi product.image thành đường dẫn thích hợp trong dữ liệu đơn hàng
                alt={product.title}
                style={{ width: "100%" }}
              />
              <p className="title mb-0">{product.title}</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  </article>
))}

               </main>   



        </div>
      </div>
    </section>
  );
};

export default Order;
