import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { useNavigate } from "react-router-dom";

const Cart = (setCartItems) => {
  const [products, setProducts] = useState([]);
  const [totalPrice, setTotalPrice] = useState(0);
  const navigate = useNavigate();
  // Retrieve data from localStorage when the component mounts
  useEffect(() => {
    const fetchCartItems = async () => {
      try {
        const response = await fetch("http://localhost:8080/api/orders", {
          method: "GET",
          headers: {
            "Content-Type": "application/json",
            // You might need to include an authorization header if required
          },
        });

        if (response.ok) {
          const cartItems = await response.json();
          setProducts(cartItems);
          // Set cart items globally (assuming setCartItems is a function passed as a prop)
          setCartItems(cartItems);
        } else {
          console.error("Failed to fetch cart items:", response.statusText);
        }
      } catch (error) {
        console.error("Error fetching cart items:", error.message);
      }
    };

    fetchCartItems();
  }, []); // The empty dependency array ensures this effect runs only once on mount
  const handleRemoveCartItem = async (productId) => {
    try {
        // Gửi yêu cầu xóa sản phẩm khỏi giỏ hàng trên máy chủ
        const response = await fetch(`http://localhost:8080/api/or ders/${productId}`, {
            method: "DELETE",
            headers: {
                "Content-Type": "application/json",
                // Bạn có thể cần thêm tiêu đề xác thực nếu cần
            },
        });

        if (response.ok) {
            // Nếu xóa thành công trên máy chủ, cập nhật lại dữ liệu sản phẩm trong giỏ hàng
            const updatedCartItems = products.filter((item) => item.id !== productId);
            setProducts(updatedCartItems);
        } else {
            console.error("Failed to remove item from cart:", response.statusText);
        }
    } catch (error) {
        console.error("Error removing item from cart:", error.message);
    }
};
  const handleUpdateQuantity = (productId, newQuantity) => {
    const updatedCartItems = products.map((item) =>
      item.id === productId ? { ...item, quantity: newQuantity } : item
    );

    setProducts(updatedCartItems);
  };

  // Function to increase the quantity
  const handleIncreaseQuantity = (productId, currentQuantity) => {
    handleUpdateQuantity(productId, currentQuantity + 1);
  };

  // Function to decrease the quantity
  const handleDecreaseQuantity = (productId, currentQuantity) => {
    if (currentQuantity > 1) {
      handleUpdateQuantity(productId, currentQuantity - 1);
    }
  };

  const handleCheckout = () => {
    // Handle checkout logic here (e.g., send order to server)
    navigate("/formcart"); // Navigate to checkout page
  };

  const handleContinueShopping = () => {
    // Navigate user to home or products page when clicking "Continue shopping"
    navigate("/");
  };
  return (
    <section class="section-content padding-y">
      <div class="container">
        <div class="row">
          <main class="col-md-9">
            <div class="card">
              {products.length > 0 &&
                products.map((row) => (
                  <table class="table table-borderless table-shopping-cart">
                    <thead class="text-muted">
                      <tr class="small text-uppercase">
                        <th scope="col">Product</th>
                        <th scope="col" width="120">
                          Quantity
                        </th>
                        <th scope="col" width="120">
                          Price
                        </th>
                        <th scope="col" class="text-right" width="200">
                          {" "}
                        </th>
                      </tr>
                    </thead>
                    <tbody>
                      <tr>
                        <td>
                          <figure class="itemside">
                            <div class="aside">
                              <img
                                src={`../images/items/${row.thumbnail}`}
                                style={{ width: "100px" }}
                              />
                            </div>
                            <figcaption class="info">
                              <a href="#" class="title text-dark">
                                {row.title}
                              </a>
                            </figcaption>
                          </figure>
                        </td>
                        <td>
                          <div class="quantity-control">
                            <button
                              onClick={() =>
                                handleDecreaseQuantity(row.id, row.quantity)
                              }
                              class="btn btn-outline-secondary btn-sm"
                            >
                              -
                            </button>
                            <span class="quantity">{row.quantity}</span>
                            <button
                              onClick={() =>
                                handleIncreaseQuantity(row.id, row.quantity)
                              }
                              class="btn btn-outline-secondary btn-sm"
                            >
                              +
                            </button>
                          </div>
                        </td>

                        <td>
                          <div class="price-wrap">
                            <var class="price">${row.price * row.quantity}</var>
                            <small class="text-muted">
                              {" "}
                              ${row.price} each{" "}
                            </small>
                          </div>
                        </td>
                        <td class="text-right">
                          <a
                            data-original-title="Save to Wishlist"
                            title=""
                            href=""
                            class="btn btn-info"
                            data-toggle="tooltip"
                          >
                            {" "}
                            <i class="fa fa-heart"></i>
                          </a>
                          <a
                            href=""
                            class="btn btn-light"
                            onClick={() => handleRemoveCartItem(row.id)}
                          >
                            {" "}
                            Remove
                          </a>
                        </td>
                      </tr>
                    </tbody>
                  </table>
                ))}
              <div className="card-body border-top">
                {products.length > 0 && (
                  <Link
                    to="/formcart"
                    className="btn btn-info float-md-right"
                    onClick={handleCheckout}
                  >
                    Mua hàng <i className="fa fa-chevron-right"></i>
                  </Link>
                )}
                <button
                  className="btn btn-light"
                  onClick={handleContinueShopping}
                >
                  <i className="fa fa-chevron-left"></i> Continue shopping
                </button>
              </div>
            </div>

            <div class="alert alert-success mt-3">
              <p class="icontext">
                <i class="icon text-success fa fa-truck"></i> Free Delivery
                within 1-2 weeks
              </p>
            </div>
          </main>
          <aside class="col-md-3">
            <div class="card mb-3">
              <div class="card-body">
                <form>
                  <div class="form-group">
                    <label>Have coupon?</label>
                    <div class="input-group">
                      <input
                        type="text"
                        class="form-control"
                        name=""
                        placeholder="Coupon code"
                      />
                      <span class="input-group-append">
                        <button class="btn btn-info">Apply</button>
                      </span>
                    </div>
                  </div>
                </form>
              </div>
            </div>
            <div class="card">
              <div class="card-body">
                <dl class="dlist-align">
                  <dt>Total price:</dt>
                  <dd class="text-right">${totalPrice}</dd>
                </dl>
                <dl class="dlist-align">
                  <dt>Discount:</dt>
                  <dd class="text-right">$0</dd>
                </dl>

                <p class="text-center mb-3">
                  <img
