import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const Login = () => {
  const [fullname, setFullname] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate();

  const handleLogin = () => {
    axios
      .post("http://localhost:8080/api/users/login", {
        fullname: fullname,
        password: password,
      })
      .then((response) => {
        if (response.status === 200) {
          // Lưu token vào localStorage
          localStorage.setItem("token", response.data);

          // Yêu cầu thông tin người dùng từ máy chủ
          axios
            .get("http://localhost:8080/api/users/login", {
              headers: {
                Authorization: `Bearer ${response.data}`,
              },
            })
            .then((response) => {
              const userData = response.data;

              // Lưu thông tin người dùng vào localStorage
              localStorage.setItem("email", userData.email);
              localStorage.setItem("phone_number", userData.phone_number);
              localStorage.setItem("address", userData.address);
              localStorage.setItem("fullname", userData.fullname);

              // Hiển thị thông báo đăng nhập thành công
              alert("Đăng nhập thành công");

              // Chuyển hướng đến trang chính
              navigate("/");
            })
            .catch((error) => {
              console.error("Error retrieving user data:", error);
            });
        } else {
          alert("Đăng nhập thất bại");
        }
      })
      .catch((error) => {
        console.error("Đăng nhập thất bại:", error);
        alert("Đăng nhập thất bại");
      });
  };

  return (
    <section className="section-content padding-y">
      <div className="card mx-auto" style={{ width: "520px" }}>
        <article className="card-body">
          <header className="mb-4">
            <h4 className="card-title">Đăng nhập</h4>
          </header>
          <form className="mx-auto">
            <div>
              <label>Họ và tên:</label>
              <input
                required="name"
                className="form-control"
                type="fullname"
                placeholder="Họ và tên"
                value={fullname}
                onChange={(e) => setFullname(e.target.value)}
              />

              <label>Mật khẩu:</label>
              <input
                required="password"
                className="form-control"
                type="password"
                placeholder="Mật khẩu"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
              />
              <div className="mt-3">
                <button
                  onClick={handleLogin}
                  type="submit"
                  className="btn btn-primary btn-block"
                >
                  Đăng nhập
                </button>
              </div>

              <div className="mt-3">
                <a href="/register" className="btn btn-outline-primary btn-block">
                  Đăng ký
                </a>
              </div>
              <a href="/forgot-password">Quên mật khẩu?</a>
            </div>
          </form>
        </article>
      </div>
    </section>
  );
};

export default Login;
